import React, {useState, useEffect} from 'react';
import {NativeSelect, FormControl } from '@material-ui/core';
import Style from './Countries.module.css';
import {fetchCountries} from '../../api/index.js';


const Countries = ({handleCountryChange}) =>{

    const [countries, setCountries] = useState([]);

    useEffect(()=>{
        const fetchAPI = async ()=> {
            setCountries( await fetchCountries() );
        };
        fetchAPI();
    }, [setCountries]);

    const useCountryChange = (event) => {
        console.log(event.target.value)
        handleCountryChange();
    }

    return(
        <FormControl>
            <NativeSelect  className={Style.nativeSelect}>
                <option value="global">Global</option>
                {countries.map((country, i)=><option id={i} value={country.name} onClick={useCountryChange}>{country.name}</option>)};
            </NativeSelect>
        </FormControl>
    );
}

export default Countries;
