import React, {useState} from 'react';
import Input from './Input';

function convert(temperature, unit){
  const temp = parseFloat(temperature);
  const realtemp = (unit == "c") ? temp * 9/5 + 32 : (temp - 32) * 5/9;
  return realtemp.toString();
  // const c = (temperature - 32) * 5/9;
  // const f = temperature * 9/5 + 32;
}


class App extends React.Component{
  constructor(props){
    super(props);
    this.handleCelsisChange = this.handleCelsisChange.bind(this);
    this.handleFahrenheitChange = this.handleFahrenheitChange.bind(this);
    this.state = {temperature: "1000", unit: "c"}
  }

  handleCelsisChange(temperature){
    this.setState({temperature, unit: "c"});
  }
  handleFahrenheitChange(temperature){
    this.setState({temperature, unit: "f"})
  }

  render(){
    const temperature = this.state.temperature;
    const unit = this.state.unit;
    let celsius;
    let fahreheit;
    if (unit = "c"){
    celsius = temperature;
    fahreheit = convert(temperature, unit);
   } else if (unit = "f") {
     celsius = convert(temperature, unit);
     fahreheit = temperature;
   }
    return(
      <div>
        <Input temperature={celsius} unit={this.state.unit} onTemChange={this.handleCelsisChange}/>
        <Input temperature={fahreheit} unit={this.state.unit} onTemChange={this.handleFahrenheitChange}/>
        <div>
          {(this.state.temprature < 100) ? "the water boil" : "the water not boil"}
        </div>
      </div>
      );
  }

}

export default App;

