import React, {useState} from 'react';
import HeaderSub from './HeaderSub';

class Header extends React.Component{

    state = {
        show : false
    }

    toggle_sub_menu = ()=>{
        this.setState({
            show : !this.state.show
        });
    };

    render(){

        return(
            <header>
                <div className="logo"></div>
                <ul className="nav">
                    <li>Home</li>
                    <li onClick={this.toggle_sub_menu}>
                        Shopping
                        {this.state.show && <HeaderSub/>}
                    </li>
                    <li>About Us</li>
                    <li>Contact Us</li>
                    <li>My Account</li>
                </ul>
            </header>
        );
    }

}

export default Header;
