import React, {useState} from 'react';
import HeaderSub from './HeaderSub';
const logo = require("../picture/logo.png");

class Header extends React.Component{

    state = {
        sub_menu_show : false,
        main_menu_show: false
    }

    toggle_sub_menu = ()=>{
        this.setState({
            sub_menu_show : !this.state.sub_menu_show
        });
    };

    toggle_main_menu = ()=>{
        this.setState({
            main_menu_show: !this.state.main_menu_show
        })
    }

    render(){

        return(
            <header>
                <div className="logo">
                    <img src={logo}/>
                </div>
                <div className="burger" onClick={this.toggle_main_menu}>
                    <div className="line"></div>
                    <div className="line"></div>
                    <div className="line"></div>
                </div>
                <ul className="nav" style={(this.state.main_menu_show) ? { color: ''}:{display: "none"}}>
                    <li>Home</li>
                    <li className="shopping-list" onClick={this.toggle_sub_menu}>
                        Shopping
                        {this.state.sub_menu_show && <HeaderSub/>}
                    </li>
                    <li>About Us</li>
                    <li>Contact Us</li>
                    <li>My Account</li>
                </ul>
            </header>
        );
    }

}

export default Header;
