import React, {useState} from 'react';

const cake_types = {
    layer_cake: '../picture/layer-cake.jpg',
    birthday_cake: '../picture/birthday-cake.jpg',
    cup_cake: '../picture/cup-cake.jpg'};

class Category extends React.Component{

    render(){

        const cake_type = this.props.type;
        const image = cake_types[cake_type];
        // src={require(cake_types['layer_cake'])}

        return(
            <section>
                <img className="category-picture"/>
                <div>{(image == '../picture/layer-cake.jpg')}</div>
                <div className="category-desc">say something</div>
            </section>
        );
    }

}

export default Category;
